
$(function(){$(document).on('change','.js-cpt-calculator input',function(){var $form=$('.js-cpt-calculator');$.ajax({url:$form.attr('action'),type:$form.attr('method'),data:$form.serialize(),success:function(response){$('.jc-cpt-inner').html(response);},error:function(response){var result;try
{var json=JSON.parse(response.responseText);$.each(json,function(index,value){result=value;});}
catch(e)
{result=response.responseText;}
$('.modal-loading').modal('hide');new Noty({'text':result,'type':'error'}).show();}});});$('.js-cpt-calculator input').trigger('change')});